import React, { useEffect, useState } from "react";
import "./StudentDashboard.css";

export default function StudentDashboard() {
  const [activeMenu, setActiveMenu] = useState("dashboard");
  const [student, setStudent] = useState(null);

  // Fetch logged-in student details
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      window.location.href = "/login";
      return;
    }

    fetch("http://localhost:5000/api/student/me", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Unauthorized");
        return res.json();
      })
      .then((data) => {
        setStudent(data.student);
      })
      .catch(() => {
        localStorage.removeItem("token");
        window.location.href = "/login";
      });
  }, []);

  const logout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  if (!student) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="profile-section">
          <img
            src={`data:image/jpeg;base64,${student.photo}`}
            alt="Profile"
            className="profile-img"
          />
          <h3>{student.name}</h3>
          <p>{student.roll_no}</p>
        </div>

        <nav className="menu">
          <button
            className={activeMenu === "dashboard" ? "active" : ""}
            onClick={() => setActiveMenu("dashboard")}
          >
            Dashboard
          </button>

          <button
            className={activeMenu === "profile" ? "active" : ""}
            onClick={() => setActiveMenu("profile")}
          >
            Profile
          </button>

          <button
            className={activeMenu === "attendance" ? "active" : ""}
            onClick={() => setActiveMenu("attendance")}
          >
            Mark Attendance
          </button>

          <button onClick={logout}>Logout</button>
        </nav>
      </aside>

      {/* Content */}
      <main className="content">
        {activeMenu === "dashboard" && (
          <div className="dashboard-view">
            <div className="notice-board">
              <h2>Notice Board</h2>
              {student.notices?.map((n, i) => (
                <div key={i} className="card">
                  <b>{n.title}</b>
                  <p>{n.message}</p>
                  <small>{n.date}</small>
                </div>
              ))}
            </div>

            <div className="timetable">
              <h2>Timetable</h2>
              <table>
                <thead>
                  <tr>
                    <th>Day</th>
                    <th>Subject</th>
                  </tr>
                </thead>
                <tbody>
                  {student.timetable?.map((t, i) => (
                    <tr key={i}>
                      <td>{t.day}</td>
                      <td>{t.subject}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeMenu === "profile" && (
          <div className="profile-card">
            <h2>Student Profile</h2>
            <p><b>Name:</b> {student.name}</p>
            <p><b>Roll No:</b> {student.roll_no}</p>
            <p><b>Semester:</b> {student.semester}</p>
            <p><b>Email:</b> {student.email}</p>
          </div>
        )}

        {activeMenu === "attendance" && (
          <div className="attendance-card">
            <h2>Mark Attendance</h2>
            <video autoPlay className="camera" />
            <button className="capture-btn">
              Capture & Mark Attendance
            </button>
          </div>
        )}
      </main>
    </div>
  );
}
