import React, { useState } from "react";
import "./Contact.css";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Message sent! We will contact you soon.");
    setFormData({ name: "", email: "", subject: "", message: "" });
  };

  return (
    <div className="contact-page">
      <Header />

      <main className="contact-container">
        <h1>Contact Us</h1>
        <p className="subtitle">
          Have questions about our Face Authentication Attendance System?  
          We’d love to hear from you.
        </p>

        <div className="contact-grid">
          {/* ===== LEFT FORM ===== */}
          <div className="contact-form">
            <h2>Send a Message</h2>

            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              <input
                type="email"
                name="email"
                placeholder="Email Address"
                value={formData.email}
                onChange={handleChange}
                required
              />

              <input
                type="text"
                name="subject"
                placeholder="Subject"
                value={formData.subject}
                onChange={handleChange}
                required
              />

              <textarea
                name="message"
                placeholder="Your Message"
                rows="5"
                value={formData.message}
                onChange={handleChange}
                required
              ></textarea>

              <button type="submit">Send Message</button>
            </form>
          </div>

          {/* ===== RIGHT INFO ===== */}
          <div className="contact-info">
            <h2>About AttendFace</h2>
            <p>
              AttendFace is a face recognition based attendance system designed
              for schools and colleges. It ensures secure, fast, and accurate
              attendance using modern AI technology.
            </p>

            <div className="info-box">
              <p><strong>Email:</strong> support@attendface.edu</p>
              <p><strong>Phone:</strong> +91 98765 43210</p>
              <p><strong>Address:</strong> School & College Campus</p>
              <p><strong>Working Hours:</strong> Mon – Fri (9 AM – 5 PM)</p>
            </div>

            <p className="team">
              Project by <strong>Shruti</strong> & <strong>Gautami</strong>
            </p>
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
}
