import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./SLogin.css";

export default function SLogin() {
  const navigate = useNavigate();
  const fileRef = useRef(null);

  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [imagePreview, setImagePreview] = useState(null);

  /* ---------------- LOGIN STATE ---------------- */
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleLogin = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(loginData),
      });

      const data = await res.json();

      if (res.ok) {
        alert("Login Successful");
        localStorage.setItem("student_id", data.user.id);
        navigate("/studentdashboard");
      } else {
        alert(data.message || "Login failed");
      }
    } catch {
      alert("Server error");
    }
  };

  /* ---------------- SIGNUP STATE ---------------- */
  const [signupData, setSignupData] = useState({
    fullName: "",
    rollNo: "",
    semester: "",
    email: "",
    password: "",
    photo: null,
  });

  const handleSignupChange = (e) => {
    setSignupData({ ...signupData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSignupData({ ...signupData, photo: file });

    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImagePreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSignup = async () => {
    const formData = new FormData();
    Object.entries(signupData).forEach(([key, value]) =>
      formData.append(key, value)
    );

    try {
      const res = await fetch("http://localhost:5000/api/auth/signup", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();

      if (res.ok) {
        alert("Signup Successful");
        setIsLogin(true);
      } else {
        alert(data.message || "Signup failed");
      }
    } catch {
      alert("Server error");
    }
  };

  /* ---------------- JSX ---------------- */
  return (
    <div className="auth-wrapper">
      <div className="auth-card">
        <h2 className="auth-title">Student Portal</h2>
        <p className="auth-subtitle">
          {isLogin ? "Login to your account" : "Create your student account"}
        </p>

        <div className="auth-tabs">
          <button type="button" className={isLogin ? "active" : ""} onClick={() => setIsLogin(true)}>
            Login
          </button>
          <button type="button" className={!isLogin ? "active" : ""} onClick={() => setIsLogin(false)}>
            Sign Up
          </button>
        </div>

        {isLogin ? (
          <div className="auth-form">
            <input
              type="email"
              name="email"
              placeholder="Email"
              onChange={handleLoginChange}
            />

            <div className="password-box">
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                placeholder="Password"
                onChange={handleLoginChange}
              />
              <span onClick={() => setShowPassword(!showPassword)}>
                {showPassword ? "üôà" : "üëÅÔ∏è"}
              </span>
            </div>

            <button type="button" className="primary-btn" onClick={handleLogin}>
              Login
            </button>
          </div>
        ) : (
          <div className="auth-form">
            <input name="fullName" placeholder="Full Name" onChange={handleSignupChange} />
            <input name="rollNo" placeholder="Roll No" onChange={handleSignupChange} />
            <input name="semester" placeholder="Semester" onChange={handleSignupChange} />
            <input type="email" name="email" placeholder="Email" onChange={handleSignupChange} />

            <div className="password-box">
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                placeholder="Password"
                onChange={handleSignupChange}
              />
              <span onClick={() => setShowPassword(!showPassword)}>
                {showPassword ? "üôà" : "üëÅÔ∏è"}
              </span>
            </div>

            <div className="upload-box" onClick={() => fileRef.current.click()}>
              {imagePreview ? (
                <img src={imagePreview} alt="preview" />
              ) : (
                <p>Click to upload student photo</p>
              )}
            </div>

            <input
              type="file"
              accept="image/*"
              ref={fileRef}
              hidden
              onChange={handleFileChange}
            />

            <button type="button" className="primary-btn" onClick={handleSignup}>
              Create Account
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
